import { createStore } from "redux";

const counterReducer = (state = { counter: 0 }, action) => {
  switch (action.type) {
    case "increment":
      return { counter: state.counter + 1 };
      case "decrement":
          if(state.counter === 0)
      return { counter: state.counter - 1 };
    default:
      return state;
  }
};

export const store = createStore(counterReducer);
